$.fn.randomize = function(b) {
    var c = b ? $(this).find(b) : $(this).children(),
        a = c.parent();
    a.each(function() { $(this).children(b).sort(function(e, d) { if ($(d).index() !== $(this).children(b).length - 1) { return Math.round(Math.random()) - 0.5 } }.bind(this)).detach().appendTo(this) });
    return this
};
$(".ourClients-slider").randomize().slick({ infinite: true, arrows: false, dots: false, slidesToShow: 5, autoplay: true, autoplaySpeed: 700, centerMode: true, variableWidth: true, swipe: false, responsive: [{ breakpoint: 768, settings: { slidesToShow: 3, swipe: true } }, { breakpoint: 480, settings: { slidesToShow: 3, swipe: true } }] });
$(".ourClients-slider").mouseover(function() { $(".ourClients-slider").slick("slickPlay") });